import React from "react";

import "./form.scss";

const Form = ({ onChange, label, ...otherProps }) => {
  return (
    <div className='group'>
      <input type='form-input' onChange={onChange} {...otherProps} />
      {label ? (
        <label
          className={`${props.value.length ? "shrink" : ""} form-input-label`}
        >
          {label}
        </label>
      ) : null}
    </div>
  );
};

export default Form;
