import React from "react";

import "./form-input.scss";

const FormInput = ({ handleChange, label, ...otherProps }) => {
  return (
    <div className='group'>
      <input type='form-input' onChange={handleChange} {...otherProps} />
      {label ? (
        <label
          className={`${
            otherProps.value.length ? "shrink" : ""
          } form-input-label`}
        >
          {label}
        </label>
      ) : null}
    </div>
  );
};

export default FormInput;
