import { takeEvery, call } from "redux-saga/effects";
import {
  firestore,
  convertCollectionsSnapshotToMap
} from "../../firebase/firebase.utils";

import { loadCollectionsSuccess, loadCollectionsFail } from "./shop.actions";

import { LOAD_COLLECTIONS_START } from "./shop.types";

export function* loadCollectionsAsync() {
  yield console.log("I am fired");

  try {
    const collectionRef = firestore.collection("collections");
    const snapshot = yield collectionRef.get();
    const collectionsMap = yield call(
      convertCollectionsSnapshotToMap,
      snapshot
    );
    yield put(loadCollectionsSuccess(collectionsMap));
  } catch (e) {
    yield put(loadCollectionsFail(error.message));
  }

  // collectionRef
  //   .get()
  //   .then(snapshot => {
  //     const collectionsMap = convertCollectionsSnapshotToMap(snapshot);
  //     dispatch(loadCollectionsSuccess(collectionsMap));
  //   })
  //   .catch(error => dispatch(loadCollectionsFail(error.message)));
}

/**
 * Saga
 */
export function* loadCollectionsStart() {
  yield takeEvery(LOAD_COLLECTIONS_START, loadCollectionsAsync);
}
